import React from "react";
import { useState } from "react";
import Hadiths from "../../hadiths.json";
import Hadith from "../hadith/hadith";
import useSound from "use-sound";
const Chapter = ({ id, index, title, hadiths }) => {
  const [isPlaying, setPlaying] = useState(false);
  const handlePlaySound = (id, hadithId) => {
    const chap = hadiths.filter((ch) => (ch.id = id));
    const h = chap.filter((h) => (h.id = hadithId));
    console.log(chap);
  };

  return (
    <div className="accordion-item shadow-sm">
      <h2 className="accordion-header" id={`heading_${id}`}>
        <button
          className={`accordion-button fs-4   ${
            index === 0 ? "" : "collapsed"
          }`}
          type="button"
          data-bs-toggle="collapse"
          data-bs-target={`#collapse_${id}`}
          aria-expanded={`${index === 0 ? "true" : "false"}`}
          aria-controls={`collapse_${id}`}
        >
          {title}
        </button>
      </h2>
      <div
        id={`collapse_${id}`}
        className={`accordion-collapse collapse  container text-dark m-2 ${
          index === 0 ? "show" : ""
        }`}
        aria-labelledby={`heading_${id}`}
        data-bs-parent="#accordionExample"
      >
        {/* ------------ chapter body ------------ */}
        <div className="accordion-body">
          {hadiths.map((hadith, i) => (
            // add haiths component here
            <Hadith
              key={hadith.id}
              id={hadith.id}
              hadithIndex={i}
              chapteSize={hadiths.length - 1}
              text={hadith.text}
              handlePlaySound={() => handlePlaySound(id, hadith.id)}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Chapter;
