import React from "react";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import ChapterList from "../chapterList/ChapterList";
import "./bookDetail.css";
import Intro from "./Intro";
export default function BookDetail() {
  const { id } = useParams();
  const [book, setBook] = useState(null);
  useEffect(() => {
    axios
      .get(`http://127.0.0.1:8000/books/${id}`)
      .then((res) => {
        setBook(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);
  if (!book) return null;

  useEffect(() => {
    axios.get(`http://127.0.0.1:8000/vocabularies/`).then((res) => {
      console.log(res);
    });
  });
  return (
    <div className="container book-detail justify-content-center ">
      <div className="row ">
        <Intro
          title={book.title}
          author={book.author}
          about={book.about_book}
          note={book.additional_notes}
        />
      </div>
      <div className="row ">
        <div className="col-xl-1 col-sm-1 "></div>
        <div className=" col-xl-12 col-sm-10">
          <ChapterList chapters={book.chapter_set} />
        </div>
        <div className="col-xl-1 col-sm-1"></div>
      </div>
    </div>
  );
}
