import React from "react";
import { useState, useContext } from "react";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Popover from "react-bootstrap/Popover";
import { DictionaryContext } from "../../Contexts/DictionaryContext";
import dictionary from "../../sources/dictionary.json";

export default function Text({ text }) {
  // const [dict] = useState(JSON.parse(JSON.stringify(dictionary)));

  const dict = useContext(DictionaryContext);
  const dict_keys = Object.keys(dict);
  return (
    <div>
      {add_definations_for_matched_words(
        split_on_matched_words(text, dict_keys),
        dict_keys,
        dict
      ).map((segment) => {
        return typeof segment === "string" ? (
          segment
        ) : (
          <OverlayTrigger
            key={`${segment[0]}_${Math.floor(Math.random(0, 100) * 1000)}`}
            placement="top"
            overlay={
              <Popover id={segment[0]}>
                <Popover.Body>
                  <div className="card-body">
                    <p className="card-text">{segment[1]}</p>
                  </div>
                </Popover.Body>
              </Popover>
            }
          >
            <span type="button" className=" text-primary">
              {segment[0]}
              <span className=" text-dark">{""}</span>
            </span>
          </OverlayTrigger>
        );
      })}
    </div>
  );
}

function split_on_matched_words(text, dict_keys) {
  const keyWords = dict_keys.join("|");
  const regex = new RegExp(`[ء-٩]*(${keyWords})[ء-٩]*`, "g");
  const matchedWords = text.match(regex);
  const splitWordsKeys = matchedWords.join("|");
  const SplitWordsRegex = new RegExp(`(${splitWordsKeys})`, "g");
  const splitWords = text.split(SplitWordsRegex);
  return splitWords;
}

function add_definations_for_matched_words(splitWords, dict_keys, dict) {
  const newText = [];
  for (const sentence of splitWords) {
    let segment = sentence;
    for (const key of dict_keys) {
      const regex = new RegExp(`[ء-٩]*${key}[ء-٩]*`, "g");
      if (sentence.match(regex, "g")) {
        segment = [sentence, dict[key]];
      }
    }
    newText.push(segment);
  }
  return newText;
}
