import React from "react";
import SoundButton from "./SoundButton";
import Text from "./Text";
export default function Hadith({
  sequence,
  hadithIndex,
  chapteSize,
  text,
  next,
  link,
  playing,
  handlePlayPause,
}) {
  return (
    <div
      className={`row fs-5  mb-2 pb-2 
      ${hadithIndex === chapteSize ? "" : "border-bottom"} `}
      key={sequence}
    >
      <div className="col-3 col-md-2  col-lg-1  border-end p-0 ">
        <span className="ps-5">{sequence}</span>

        <br />
        <div className=" ps-5">
          <SoundButton
            sequence={sequence}
            link={link}
            playing={playing}
            next={next}
            handlePlayPause={handlePlayPause}
          />
        </div>
        <br />
        <span className="ps-3">(صحيح)</span>
      </div>
      <div className=" col-9 col-md-10 col-lg-9 mt-3">
        <Text text={text} />
      </div>
    </div>
  );
}
