import "./App.css";
import Chapter from "./components/chapter/chapter";
import Chapters from "../../hadiths.json";
function App() {
  const chapters = JSON.parse(JSON.stringify(Chapters));
  return (
    <div className="container">
      <div>مقدمة</div>
      {/* ------------------Chapters------------------------ */}
      <div className="accordion" id="accordionExample">
        {/* -------------Chapter section --------------------- */}
        {chapters.map((chapter, index) => (
          <Chapter
            key={chapter.id}
            id={chapter.id}
            title={chapter.title}
            hadiths={chapter.hadiths}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
