import React from "react";
import useSound from "use-sound";
import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlayCircle, faPauseCircle } from "@fortawesome/free-solid-svg-icons";

export default function SoundButton({
  id,
  link,
  playing,
  next,
  handlePlayPause,
}) {
  const [play, { pause }] = useSound(link, {
    volume: 0.55,
    onend: () => {
      setTimeout(() => handlePlayPause(next), 500);
    },
  });
  const handlePlaySound = (playing) => {
    if (playing) {
      pause();
      setPlaying(!playing);
    } else {
      play();
      setPlaying(!playing);
    }
  };
  const player = () => {
    return playing ? play() : stop();
  };
  return (
    <FontAwesomeIcon
      icon={playing ? faPauseCircle : faPlayCircle}
      size="xl"
      transform="move: right-6 down-4"
      onClick={() => handlePlayPause(id)}
      className={playing ? "text-success" : ""}
      player={player()}
    ></FontAwesomeIcon>
  );
}
