import React from "react";
import Hadith from "../hadith/Hadith";

export default function HadithList({ hadiths, handlePlaySound }) {
  return (
    <>
      <div className="accordion-body">
        {hadiths.map((hadith, i) => (
          // add haiths component here
          <Hadith
            key={hadith.id}
            id={hadith.id}
            hadithIndex={i}
            chapteSize={hadiths.length - 1}
            text={hadith.text}
            handlePlaySound={() => handlePlaySound(hadith.id)}
          />
        ))}
      </div>
    </>
  );
}
