  return (
    <div
      className={`row fs-5  mb-2 pb-2
      ${i === chapter.hadiths.length - 1 ? "" : "border-bottom"}`}
      key={id}
    >
      <div className="col-1 rounded-2 border-end ">
        <span className="">{id}</span>
        <br />
        <HadithSound sound={sound} />
      </div>
      <div className="col-11">
        <p className="text-dark">{text}</p>
      </div>
    </div>
  );
}
