import React from "react";
import Chapters from "../../sources/hadiths.json";
import { useState } from "react";
import Chapter from "../chapter/chapter";
import { useSound } from "use-sound";
export default function ChapterList() {
  const [chapters, setChapters] = useState(
    JSON.parse(JSON.stringify(Chapters))
  );
  const [play, stop, setLink] = useSound("");
  const soundPlayer = (src, playing) => {
    setLink(src);
    if (playing) {
      stop();
    } else {
      play();
    }
  };
  const handlePlaySound = (chapterId, hadithId) => {
    const updatedChapters = chapters.map((chapter) => {
      if (chapter.id === chapterId) {
        for (let h of chapter.hadiths) {
          if (h.id === hadithId) {
            soundPlayer(`sounds/s_${chapterId}_h_${hadithId}.mp3`, h.playing);
            h.playing = !h.playing;
          } else {
          }
        }
      }
      return { ...chapter };
    });
    setChapters(updatedChapters);
    // const hadith = chapter.hadiths.filter((h) => h.id == hadithId);
    // console.log(hadith);
  };
  return (
    <div className="accordion" id="accordionExample">
      {chapters.map((chapter, index) => (
        <Chapter
          key={chapter.id}
          id={chapter.id}
          title={chapter.title}
          hadiths={chapter.hadiths}
          index={index}
          handlePlaySound={handlePlaySound}
        />
      ))}
    </div>
  );
}
