import React from "react";
import { useState } from "react";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Popover from "react-bootstrap/Popover";
import dictionary from "../../sources/dictionary.json";
export default function Text({ text }) {
  const [dict] = useState(JSON.parse(JSON.stringify(dictionary)));

  return (
    <div>
      <p className="text-dark">
        {text.split(" ").map((word, index) => {
          if (dict[word]) {
            return (
              <>
                <OverlayTrigger
                  placement="top"
                  overlay={
                    <Popover id={word}>
                      <Popover.Body>{dict[word].des}</Popover.Body>
                    </Popover>
                  }
                >
                  <span type="button" className=" text-primary">
                    {word}
                  </span>
                </OverlayTrigger>
              </>
            );
          } else {
            return <span key={`${word}_${index}`}>{word}</span>;
          }
          <span key={`${word}_${index}`}>
            {dict[word] ? (
              <OverlayTrigger
                placement="top"
                overlay={
                  <Popover id={word}>
                    <Popover.Body>{dict[word].des}</Popover.Body>
                  </Popover>
                }
              >
                <span type="button" className=" text-primary">
                  {word}
                </span>
              </OverlayTrigger>
            ) : (
              word
            )}
          </span>;
        })}
      </p>
    </div>
  );
}
