import { random } from "fontawesome";
import React from "react";
import { useState } from "react";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Popover from "react-bootstrap/Popover";
import dictionary from "../../sources/dictionary.json";
export default function Text({ text }) {
  const [dict] = useState(JSON.parse(JSON.stringify(dictionary)));

  return (
    <div>
      {Object.entries(dict).map(([k]) => {
        let words = new RegExp(`[،-٩]+${key}`, "g");
        let keywords = text.match(words);
        if (keywords) keywords.map((word) => <p>{word}</p>);
        <span>{key}</span>;
      })}
      {/* {Object.keys(dict).forEach((key) => {

      })} */}
    </div>
  );
}
