import React from "react";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import ChapterList from "../chapterList/ChapterList";
// import Chapters from "../../sources/hadiths.json";
import "./bookDetail.css";
export default function BookDetail() {
  const { id } = useParams();
  const [book, setBook] = useState([]);
  useEffect(() => {
    axios
      .get(`http://127.0.0.1:8000/books/${id}`)
      .then((res) => {
        setBook(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);
  return (
    <div className="container book-detail ">
      <div className="row ">
        <div className="col-xl-1 col-sm-1 "></div>
        <div className=" col-xl-12 col-sm-10">
          {/* <ChapterList chapters={book.chapter_set} /> */}
        </div>
        <div className="col-xl-1 col-sm-1"></div>
      </div>
    </div>
  );
}
