import React from "react";
import { useState } from "react";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Popover from "react-bootstrap/Popover";
import dictionary from "../../sources/dictionary.json";
export default function Text({ text }) {
  const [dict] = useState(JSON.parse(JSON.stringify(dictionary)));

  return (
    <div>
      <p className="text-dark">
        {text.split(/( )/g).map((word, index) => {
          if (word.includes("،")) {
            let stripedWord = word.split(/،/)[0];
            if (dict[stripedWord]) {
              return (
                <OverlayTrigger
                  key={`${stripedWord}_${index}`}
                  placement="top"
                  overlay={
                    <Popover id={stripedWord}>
                      <Popover.Body>
                        {dict[stripedWord].image === "" ? (
                          ""
                        ) : (
                          <img
                            width={90}
                            height={90}
                            src={dict[stripedWord].image}
                            className="card-img-top"
                            alt=""
                          />
                        )}
                        <div className="card-body">
                          <p className="card-text">{dict[stripedWord].des}</p>
                        </div>
                      </Popover.Body>
                    </Popover>
                  }
                >
                  <span type="button" className=" text-primary">
                    {stripedWord}
                    <span className=" text-dark">{"،"}</span>
                  </span>
                </OverlayTrigger>
              );
            } else {
              return <span key={`${word}_${index}`}>{word}</span>;
            }
          } else {
            return <span key={`${word}_${index}`}>{word}</span>;
          }
        })}
      </p>
    </div>
  );
}
