import React from "react";
import { useState } from "react";
import dictionary from "../../sources/dictionary.json";
export default function Text({ text }) {
  const [dict] = useState(JSON.parse(JSON.stringify(dictionary)));
  console.log(dict["الْبَائِنِ"]);
  return (
    <div>
      <p className="text-dark">
        {text.split(" ").map((word) => (
          <span> {word}</span>
        ))}
      </p>
    </div>
  );
}
