import React from "react";
import { useState } from "react";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Popover from "react-bootstrap/Popover";
import dictionary from "../../sources/dictionary.json";
export default function Text({ text }) {
  const [dict] = useState(JSON.parse(JSON.stringify(dictionary)));

  return (
    <div>
      <OverlayTrigger
        trigger="click"
        placement="top"
        overlay={
          <Popover id="popover-basic">
            <Popover.Header as="h3">Popover right</Popover.Header>
            <Popover.Body>
              And here's some <strong>amazing</strong> content. It's very
              engaging. right?
            </Popover.Body>
          </Popover>
        }
      >
        <span className="text-info">Click me to see</span>
      </OverlayTrigger>
      <p className="text-dark">
        {text.split(" ").map((word, index) => (
          <span key={`${word}_${index}`}> {dict[word] ? "klklk" : word}</span>
        ))}
      </p>
    </div>
  );
}
