import React from "react";
import SoundButton from "./SoundButton";
import Text from "./Text";
export default function Hadith({
  sequence,
  hadithIndex,
  chapteSize,
  text,
  next,
  link,
  playing,
  handlePlayPause,
}) {
  return (
    <div
      className={`row fs-5  mb-2 pb-2 
      ${hadithIndex === chapteSize ? "" : "border-bottom"} `}
      key={sequence}
    >
      <div className="col-2 col-md-1  col-sm-3 rounded-2 border-end p-0 ">
        <span className="">{sequence}.</span>
        <br />
        <span className="ps-0">(صحيح)</span>
        <br />
        <SoundButton
          sequence={sequence}
          link={link}
          playing={playing}
          next={next}
          handlePlayPause={handlePlayPause}
        />
      </div>
      <div className=" col-10 col-md-11 col-sm-9">
        <Text text={text} />
      </div>
    </div>
  );
}
