import React from "react";
import { useState } from "react";

export default function Intro() {
  const [basmalah] = useState("بسم الله الرحمن الرحيم ");
  const [introduction] = useState("مقدمة");
  const [aboutBook] = useState(
    "الحمد لله رب العالمين والصلاة والسلام على سيد المرسلين، وعلى آله وصحبه اجمعين، ومن تبعهم بإحسان الى يوم الدين، أما بعد: \n" +
      " هذه النسخه من كتاب الشمائل المحمدية للامام الحافظ ابي عيسى الترمذي، هيا منسوخة من كراسة الشمائل المحمدية للشيخ الدكتور حسن الحسيني حفظه الله، التي اطلعنا عليها في شهر رمضان للعام ١٤٤١ هجري ، الموافق شهر مايو للعام ٢٠٢٠ ميلاي. " +
      "وقد  اضفنا الى الكتاب المقرواء "
  );
  const [aboutAuthor] = useState("");
  console.log(basmalah);
  return (
    <>
      <div className="text-xl-center basmalahFont mb-2">{basmalah}</div>
      <div className=" text-xl-center fs-2 mb-2 ">{introduction}</div>
      <div className="displayLinebreak  fs-5 mb-2 ">{aboutBook}</div>
      <div className="displayLinebreak text-xl-center fs-2 mb-2 ">
        {aboutAuthor}
      </div>
    </>
  );
}
