import React from "react";
import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPlayCircle,
  faPauseCircle,
  faRotateLeft,
} from "@fortawesome/free-solid-svg-icons";
import Hadiths from "../../hadiths.json";
import useSound from "use-sound";
import { useRef } from "react";

const Chapter = () => {
  const [chapters] = useState(JSON.parse(JSON.stringify(Hadiths)));
  const [isPlaying, setPlaying] = useState(false);
  const [play, { stop, pause }] = useSound("sounds/s_1_h_01.mp3");
  const [pausePlayButton] = useRef(null);
  return (
    <div className="accordion" id="accordionExample">
      {chapters.map((chapter, index) => (
        <div className="accordion-item shadow-sm" key={chapter.id}>
          <h2 className="accordion-header" id={`heading_${chapter.id}`}>
            <button
              className={`accordion-button fs-4   ${
                index === 0 ? "" : "collapsed"
              }`}
              type="button"
              data-bs-toggle="collapse"
              data-bs-target={`#collapse_${chapter.id}`}
              aria-expanded={`${index === 0 ? "true" : "false"}`}
              aria-controls={`collapse_${chapter.id}`}
            >
              {chapter.title}
            </button>
          </h2>
          <div
            id={`collapse_${chapter.id}`}
            className={`accordion-collapse collapse  container text-dark m-2 ${
              index === 0 ? "show" : ""
            }`}
            aria-labelledby={`heading_${chapter.id}`}
            data-bs-parent="#accordionExample"
          >
            {/* ------------ chapter body ------------ */}
            <div className="accordion-body">
              {chapter.hadiths.map((hadith, i) => (
                <div
                  className={`row fs-5  mb-2 pb-2
                    ${i === chapter.hadiths.length - 1 ? "" : "border-bottom"}`}
                  key={hadith.id}
                >
                  <div className="col-1 rounded-2 border-end ">
                    <span className="">{hadith.id}</span>
                    <br />
                    <FontAwesomeIcon
                      icon={faRotateLeft}
                      size="xl"
                      transform="move: right-4  down-4"
                      onClick={() => {
                        console.log(pausePlayButton);
                        stop();
                      }}
                    ></FontAwesomeIcon>
                    <FontAwesomeIcon
                      ref={pausePlayButton}
                      icon={isPlaying ? faPauseCircle : faPlayCircle}
                      size="xl"
                      transform="move: right-2 down-4"
                      onClick={() => {
                        setPlaying(!isPlaying);
                      }}
                      onChange={() => {
                        isPlaying ? pause() : play();
                      }}
                    ></FontAwesomeIcon>
                    {/* <audio src={hadith.sound} controls="Play, Pause"></audio>
                    <FontAwesomeIcon
                      icon={faPlayCircle}
                      size="2xl"
                      transform="move: down-8 right-6"
                      onClick={""}
                    ></FontAwesomeIcon> */}
                  </div>
                  <div className="col-11">
                    <p className="text-dark">{hadith.text}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Chapter;
