import React from "react";

export default function Text({ text }) {
  return (
    <div>
      <p className="text-dark">
        {text.split(/(كُنْ رَسُولُ*)/g).map((word) => (
          <span> {word}</span>
        ))}
      </p>
    </div>
  );
}
