import React from "react";
const state = {
  chapters: [
    { id: 1, title: "باب ما جاء في خَلق رسول الله صلى الله عليه وسلم" },
    { id: 2, title: " باب ما جاء في خاتم اليبوة" },
  ],
};

const Chapter = () => {
  return (
    <div className="accordion" id="accordionExample">
      {state.chapters.map((chapter, index) => (
        <div className="accordion-item" key={chapter.id}>
          <h2 className="accordion-header" id={`heading_${chapter.id}`}>
            <button
              className={`accordion-button button chapter_heading_bg ${
                index === 0 ? "" : "collapsed"
              }`}
              type="button"
              data-bs-toggle="collapse"
              data-bs-target={`#collapse_${chapter.id}`}
              aria-expanded="true"
              aria-controls={`collapse_${chapter.id}`}
            >
              {chapter.title}
            </button>
          </h2>
          <div
            id={`collapse_${chapter.id}`}
            className={`accordion-collapse collapse ${
              index === 0 ? "show" : ""
            }`}
            aria-labelledby={`heading_${chapter.id}`}
            data-bs-parent="#accordionExample"
          >
            {/* ------------ chapter body ------------ */}
            <div className="accordion-body m-0 p-0"></div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Chapter;
