import React from "react";
import { useState } from "react";
import dictionary from "../../sources/dictionary.json";
import { MDBPopover, MDBPopoverBody, MDBPopoverHeader } from "mdb-react-ui-kit";
export default function Text({ text }) {
  const [dict] = useState(JSON.parse(JSON.stringify(dictionary)));
  return (
    <div>
      <p className="text-dark">
        <MDBPopover
          size="lg"
          color="danger"
          btnChildren="Click to toggle popover"
        >
          <MDBPopoverHeader>Popover title</MDBPopoverHeader>
          <MDBPopoverBody>
            And here's some amazing content. It's very engaging. Right?
          </MDBPopoverBody>
        </MDBPopover>

        {text.split(" ").map((word, index) => (
          <span key={`${word}_${index}`}> {dict[word] ? "klklk" : word}</span>
        ))}
      </p>
    </div>
  );
}
