import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlayCircle, faPauseCircle } from "@fortawesome/free-solid-svg-icons";
import useSound from "use-sound";
import { useState } from "react";
export default function SoundButton({ mediaSrc }) {
  const [playing, setPlaying] = useState(false);
  const [play, { pause }] = useSound(mediaSrc, {
    volume: 0.75,
    onend: function () {
      setPlaying(false);
    },
  });
  const handlePlaySound = (playing) => {
    if (playing) {
      pause();
      setPlaying(!playing);
    } else {
      play();
      setPlaying(!playing);
    }
  };
  return (
    <FontAwesomeIcon
      icon={playing ? faPauseCircle : faPlayCircle}
      size="xl"
      transform="move: right-6 down-4"
      onClick={() => handlePlaySound(playing)}
    ></FontAwesomeIcon>
  );
}
