import React from "react";
import { useState } from "react";
import Hadiths from "../../hadiths.json";
import Hadith from "../hadith/hadith";
import useSound from "use-sound";
const Chapter = () => {
  const [isPlaying, setPlaying] = useState(false);
  const handlePlaySound = (e) => {
    console.log(e);
    setPlaying(!isPlaying);
  };

  return (
    <div className="accordion-item shadow-sm" key={chapter.id}>
      <h2 className="accordion-header" id={`heading_${chapter.id}`}>
        <button
          className={`accordion-button fs-4   ${
            index === 0 ? "" : "collapsed"
          }`}
          type="button"
          data-bs-toggle="collapse"
          data-bs-target={`#collapse_${chapter.id}`}
          aria-expanded={`${index === 0 ? "true" : "false"}`}
          aria-controls={`collapse_${chapter.id}`}
        >
          {chapter.title}
        </button>
      </h2>
      <div
        id={`collapse_${chapter.id}`}
        className={`accordion-collapse collapse  container text-dark m-2 ${
          index === 0 ? "show" : ""
        }`}
        aria-labelledby={`heading_${chapter.id}`}
        data-bs-parent="#accordionExample"
      >
        {/* ------------ chapter body ------------ */}
        <div className="accordion-body">
          {chapter.hadiths.map((hadith, i) => (
            // add haiths component here
            <Hadith
              key={hadith.id}
              id={hadith.id}
              hadithIndex={i}
              chapteSize={chapter.hadiths.length - 1}
              text={hadith.text}
              handlePlaySound={handlePlaySound}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Chapter;
