import React from "react";
import useSound from "use-sound";
import { useState } from "react";
import SoundButton from "./SoundButton";
export default function Hadith({
  id,
  text,
  hadithIndex,
  chapteSize,
  mediaSrc,
}) {
  const [playing, setPlaying] = useState(false);
  const [play, { pause }] = useSound(mediaSrc, {
    volume: 0.75,
    onend: function () {
      setPlaying(false);
    },
  });
  const handlePlaySound = (playing) => {
    if (playing) {
      pause();
      setPlaying(!playing);
    } else {
      play();
      setPlaying(!playing);
    }
  };

  return (
    <div
      className={`row fs-5  mb-2 pb-2 bg-light
      ${hadithIndex === chapteSize ? "" : "border-bottom"}`}
      key={id}
    >
      <div className="col-1 rounded-2 border-end ">
        <span className="">{id}</span>
        <br />
        <SoundButton playing={playing} handlePlaySound={handlePlaySound} />
      </div>
      <div className="col-11">
        <p className="text-dark">
          {/* {text.split(" ").map((word) => (
            <span> {word}</span>
          ))} */}
          {text}
        </p>
      </div>
    </div>
  );
}
