import React from "react";
import HadithSound from "../hadithSound/hadithSound";
export default function Hadith({ id, text, sound, hadithIndex, chapteSize }) {
  handlePlaySound = () => {
    console.log(sound);
  };
  return (
    <div
      className={`row fs-5  mb-2 pb-2
      ${hadithIndex === chapteSize ? "" : "border-bottom"}`}
      key={id}
    >
      <div className="col-1 rounded-2 border-end ">
        <span className="">{id}</span>
        <br />
        <HadithSound handlePlaySound={handlePlaySound} />
      </div>
      <div className="col-11">
        <p className="text-dark">
          {text.split(" ").map((word) => (
            <span> {word}</span>
          ))}
        </p>
      </div>
    </div>
  );
}
