import React from "react";
const state = {
  chapters: [
    {
      id: 1,
      title: "باب ما جاء في خَلق رسول الله صلى الله عليه وسلم",
      hadiths: [
        {
          id: 1,
          text: "حَدَّثَنَا أَبُو رَجَاءٍ قُتَيْبَةُ بْنُ سَعِيدٍ، عَنْ مَالِكِ بْنِ أَنَسٍ، عَنْ رَبِيعَةَ بْنِ أَبِي عَبْدِ الرَّحْمَنِ، عَنْ أَنَسِ بْنِ مَالِكٍ، أَنَّهُ سَمِعَهُ، يَقُولُكَانَ رَسُولُ اللهِ صلى الله عليه وسلم، لَيْسَ بِالطَّوِيلِ الْبَائِنِ، وَلاَ بِالْقَصِيرِ، وَلاَ بِالأَبْيَضِ الأَمْهَقِ، وَلاَ بِالآدَمِ، وَلاَ بِالْجَعْدِ الْقَطَطِ، وَلاَ بِالسَّبْطِ، بَعَثَهُ اللَّهُ تَعَالَى عَلَى رَأْسِ أَرْبَعِينَ سَنَةً، فَأَقَامَ بِمَكَّةَ عَشْرَ سِنِينَ، وَبِالْمَدِينَةِ عَشْرَ سِنِينَ، وَتَوَفَّاهُ اللَّهُ تَعَالَى عَلَى رَأْسِ سِتِّينَ سَنَةً، وَلَيْسَ فِي رَأْسِهِ وَلِحْيَتِهِ عِشْرُونَ شَعَرَةً بَيْضَاءَ",
        },
      ],
    },
    { id: 2, title: " باب ما جاء في خاتم النبوة" },
    { id: 3, title: "باب ما جاء في شعر رسول الله صلى الله عليه وسلم" },
    { id: 4, title: "باب ما جاء في ترجل رسول الله صلى الله عليه وسلم" },
  ],
};

const Chapter = () => {
  return (
    <div className="accordion" id="accordionExample">
      {state.chapters.map((chapter, index) => (
        <div className="accordion-item shadow" key={chapter.id}>
          <h2 className="accordion-header" id={`heading_${chapter.id}`}>
            <button
              className={`accordion-button chapter_heading_bg ${
                index === 0 ? "" : "collapsed"
              }`}
              type="button"
              data-bs-toggle="collapse"
              data-bs-target={`#collapse_${chapter.id}`}
              aria-expanded={`${index === 0 ? "true" : "false"}`}
              aria-controls={`collapse_${chapter.id}`}
            >
              {chapter.title}
            </button>
          </h2>
          <div
            id={`collapse_${chapter.id}`}
            className={`accordion-collapse collapse ${
              index === 0 ? "show" : ""
            }`}
            aria-labelledby={`heading_${chapter.id}`}
            data-bs-parent="#accordionExample"
          >
            {/* ------------ chapter body ------------ */}
            <div className="accordion-body m-0 p-0">
              {chapter.hadiths.map((hadith) => (
                <p key={hadith.id}>{hadith.text}</p>
              ))}
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Chapter;
