import { random } from "fontawesome";
import React from "react";
import { useState } from "react";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Popover from "react-bootstrap/Popover";
import dictionary from "../../sources/dictionary.json";
import dictionary_keys from "../../sources/dictionary_keys.json";
export default function Text({ text }) {
  const [dict] = useState(JSON.parse(JSON.stringify(dictionary)));
  const [dict_keys] = useState(JSON.parse(JSON.stringify(dictionary_keys)));
  return <div>{(matched_words = get_all_matched_words(text, dict_keys))}</div>;
}

function get_all_matched_words(text, dict_keys) {
  const words = text.split(" ");
  let matched_words = [];
  for (const word of words) {
    for (const key of dict_keys) {
      const regex = new RegExp(`[ء-٩]*(${key})[ء-٩]*`, "g");
      if (word.match(regex)) {
        matched_words.push([word, key]);
      }
    }
  }

  return matched_words;
}
